mapper.insert:
INSERT INTO jhbbs_email (
userid, pass, toemail,
sendemail, title, content, hit, cipp, regdate, del
) VALUES (
#{userid}, #{pass}, #{toemail}, #{sendemail}, #{title}, #{content},
#{hit}, #{cipp}, #{regdate}, #{del}
)

mapper.update:
update jhbbs_email set
title=#{title},
content=#{content},
cipp=#{cipp}
where
email_idx=#{email_idx}
